// SearchForm.js
import React, { useState } from 'react';
import './SearchForm.css';

const SearchForm = () => {
  const [fromCity, setFromCity] = useState('');
  const [toCity, setToCity] = useState('');
  const [fromAirport, setFromAirport] = useState('');
  const [toAirport, setToAirport] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [numberOfAdults, setNumberOfAdults] = useState(1); // Default to 1 adult

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Perform the search or submit action with the form data
    // You can add your logic here
    console.log('Form submitted:', {
      fromCity,
      toCity,
      fromAirport,
      toAirport,
      departureDate,
      returnDate,
      numberOfAdults,
    });

    try {
      const response = await fetch('http://localhost:3001/flight-search', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fromCity,
          toCity,
          fromAirport,
          toAirport,
          departureDate,
          returnDate,
          numberOfAdults,
          // Add other form data as needed
        }),
      });

      if (response.ok) {
        const flightResults = await response.json();
        console.log('Flight search results:', flightResults);
        // Handle the flight results as needed
      } else {
        console.error('Failed to send search request:', response);
        // Handle the error condition
      }
    } catch (error) {
      console.error('Error sending search request:', error);
      // Handle the error condition
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <label>
            From City:
            <input
              type="text"
              value={fromCity}
              onChange={(e) => setFromCity(e.target.value)}
            />
          </label>

          <label>
            To City:
            <input
              type="text"
              value={toCity}
              onChange={(e) => setToCity(e.target.value)}
            />
          </label>

          <label>
            From Airport:
            <input
              type="text"
              value={fromAirport}
              onChange={(e) => setFromAirport(e.target.value)}
            />
          </label>

          <label>
            To Airport:
            <input
              type="text"
              value={toAirport}
              onChange={(e) => setToAirport(e.target.value)}
            />
          </label>

          <label>
            Departure Date:
            <input
              type="date"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
            />
          </label>

          <label>
            Return Date:
            <input
              type="date"
              value={returnDate}
              onChange={(e) => setReturnDate(e.target.value)}
            />
          </label>

          <label>
            Number of Adults:
            <input
              type="number"
              value={numberOfAdults}
              onChange={(e) => setNumberOfAdults(e.target.value)}
              min="1"
            />
          </label>

          <button type="submit">Search</button>
        </form>
      </div>
    </div>
  );
};

export default SearchForm;
